import React, { useState } from 'react';
import DataInterface from '../services/DataInterface';

function Tag({id, tag }) {
  const [buttonColor, setButtonColor] = useState('blue');

  // Funci贸n para cambiar el color del bot贸n
  const toggleButtonColor = () => {
    setButtonColor(prevColor => {
        if(prevColor === 'blue') {
            DataInterface.removeTag(id);
            return 'white';
        }
        else {
            DataInterface.addTag(id);
            return 'blue';
        }
    });
  };

  return (
    <button
      onClick={toggleButtonColor}
      style={{ backgroundColor: buttonColor, color: 'white' }}
    >
      {tag}
    </button>
  );
}

export default Tag;
