import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import TagCreation from '../components/TagCreation';

function CreatePost() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [author, setAuthor] = useState('');
  const [tags, setTags] = useState([]);
  const navigate = useNavigate();

  const handleTagCreate = (newTag) => {
    // Asignar lógica para agregar la etiqueta a la base de datos o estado local
    console.log('New tag created:', newTag);
    // Aquí podrías agregar la etiqueta al array de etiquetas si estás manejando el estado localmente
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí iría la lógica para enviar los datos al backend
    console.log('Creating post with:', title, content, author, tags);
    navigate('/feed'); // Redirige al feed principal tras crear el post
  };

  return (
    <div>
      <h1>Create Post</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </label>
        <br />
        <label>
          Content:
          <textarea value={content} onChange={(e) => setContent(e.target.value)} />
        </label>
        <br />
        <label>
          Author:
          <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} />
        </label>
        <br />
        <label>
          Tags:
          <input type="text" value={tags} onChange={(e) => setTags(e.target.value)} />
        </label>
        <br />
        <TagCreation onTagCreate={handleTagCreate} />
        <button type="submit">Create Post</button>
      </form>
    </div>
  );
}

export default CreatePost;
