import React, {useState, useEffect} from 'react';
import { get_tags_by_post } from '../services/Api';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';
import NonEditableTag from './NonEditableTag';
function Post({data }) {
  const { user } = useAuth();
  const [tags, setTags] = useState([]);
  const navigate = useNavigate();
  useEffect(() =>{
    const cargarTags = async() =>{
      try{
        const data1 = await get_tags_by_post(data.id, user.access_token);
        setTags(data1.data);
      }
      catch(error){
        console.error('Error fetching tags', error);
        if(error.response.status === 401){
          navigate('/login');
        }
      }
    };
    cargarTags();
  },[data]);


  return (
    <div>
      <h2>{data.title}</h2>
      <p>{data.content}</p>
      <small>By {data.author_id} on {data.publication_date}</small>
      <h2>With Calification {data.avg_calification} of {data.amount_califications} users</h2>
      <h2>Tags</h2>
      {tags.map((tag) => (
        <NonEditableTag key={tag.id} name={tag.name} />
      ))}
    </div>
  );
}

export default Post;
