import React, { useState, useEffect } from 'react';
import { get_tags_by_post, get_my_calification_on_post, get_user, update_calification, calificate_post } from '../services/Api';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';
import NonEditableTag from './NonEditableTag';

function Post({ data, onRefresh }) {
  const [username, setUsername] = useState('');
  const { user } = useAuth();
  const [tags, setTags] = useState([]);
  const [myCalification, setMyCalification] = useState(0);
  const navigate = useNavigate();
  const [calification_id, setCalification_id] = useState(0);
  const [date, setDate] = useState('');

  useEffect(() => {
    const formatDate = new Date(data.publication_date).toISOString().split('T')[0];
    setDate(formatDate);

    const fetchData = async () => {
      const tagsData = await get_tags_by_post(data.id, user.access_token);
      setTags(tagsData.data || []);
      const userData = await get_user(data.author_id, user.access_token);
      setUsername(userData.username);
      const calData = await get_my_calification_on_post(data.id, user.access_token);
      setMyCalification(calData.data.length ? calData.data[0].calification : -1);
      setCalification_id(calData.data.length ? calData.data[0].id : 0);
    };
    fetchData();
  }, [onRefresh, user.access_token, data.id, data.author_id]);

  const handleCalification = async (rating) => {
    const cal = parseInt(rating);
    if (cal > 0 && cal <= 5) {
      if (calification_id) {
        await update_calification(calification_id, data.id, cal, user.access_token);
      } else {
        await calificate_post(data.id, cal, user.access_token);
      }
      setMyCalification(cal);
      onRefresh();  // Assuming this refreshes parent component data
    }
  };

  return (
    <div className='post-container'>
      <div className='post-head'>
        <h2>{data.title}</h2>
        <div className="post-head-grid-container">
          <div>
            <small>Author: </small>
            <Link to={`/profile/${data.author_id}`}>{username}</Link>
          </div>
          <div>
            <small>Fecha de publicación: {date}</small>
          </div>
        </div>
      </div>
      <div className='post-body'>
        <p>{data.content}</p>
        <h3>Tags</h3>
        {tags.map(tag => (
          <NonEditableTag key={tag.id} name={tag.name} />
        ))}
        <h3>Calificacion: {data.avg_calification}</h3>
        <small>{data.amount_califications} usuarios han calificado este Post</small>
        <h3>Tu calificación es {myCalification}</h3>
        {[1, 2, 3, 4, 5].map(star => (
          <span key={star} className={star <= myCalification ? 'rated' : 'unrated'} onClick={() => handleCalification(star)}>
            ★
          </span>
        ))}
        <button onClick={() => navigate(`/post/${data.id}`)}>Detalles</button>
      </div>
    </div>
  );
}

export default Post;
